import xbmc,xbmcaddon,xbmcgui,xbmcplugin,urllib,urllib2,os,re,sys,datetime,hashlibimport base64import HTMLParserimport osfrom resources.libs.modules import satoolsaddon_id        = 'plugin.video.streamarmy'AddonTitle      = "[COLOR red][B]STREAM ARMY[/B][/COLOR]"fanart          = xbmc.translatePath(os.path.join('special://home/addons/' + addon_id , 'fanart.jpg'))fanarts         = xbmc.translatePath(os.path.join('special://home/addons/' + addon_id , 'fanart.jpg'))icon            = xbmc.translatePath(os.path.join('special://home/addons/' + addon_id, 'icon.png'))dialog          = xbmcgui.Dialog()dp              = xbmcgui.DialogProgress()def SCRAPE_DISNEY_FILMS():    url = 'http://www.freecartoonszone.info/p/list-of-disney-movies-z.html'    link = satools.open_url(url)    match = re.compile('<div class="separator"(.+?)</div>').findall(link)    for links in match:        try:            url = re.compile ('<a href="(.+?)"').findall(links)[0]            title = re.compile ('<b>(.+?)</b>').findall(links)[0]            icon = 'http://i.imgur.com/ZiDSoQN.png'            satools.addDir("[COLOR silver]" + title + "[/COLOR]",url,28,icon,fanart,'')        except:pass        def SCRAPE_DISNEY_LINKS(url):    link = satools.open_url(url)    match = re.compile ("<div class='entry'>(.+?)</center>").findall(link)    for links in match:        try:            url = re.compile ('src="(.+?)"').findall(links)[1].replace('https://href.li/?', '')            icon = re.compile ('src="(.+?)"').findall(links)[0]            fanart = re.compile ('src="(.+?)"').findall(links)[0]            title = re.compile ('<u>(.+?)</u>').findall(links)[0].replace('Disney Movie Full Watch Online Free', '')            satools.addLink("[COLOR silver]" + title + "[/COLOR]",url,2,icon,fanart,'')        except:pass