import xbmc,xbmcaddon,xbmcgui,xbmcplugin,urllib,urllib2,os,re,sys,datetime,hashlibimport base64import HTMLParserimport osfrom resources.libs.modules import satoolsaddon_id        = 'plugin.video.streamarmy'AddonTitle      = "[COLOR red][B]STREAM ARMY[/B][/COLOR]"fanart          = xbmc.translatePath(os.path.join('special://home/addons/' + addon_id , 'fanart.jpg'))fanarts         = xbmc.translatePath(os.path.join('special://home/addons/' + addon_id , 'fanart.jpg'))icon            = xbmc.translatePath(os.path.join('special://home/addons/' + addon_id, 'icon.png'))dialog          = xbmcgui.Dialog()dp              = xbmcgui.DialogProgress()def SCRAPE_NEWMOVIESONLINE_CONTENT(url):    link = satools.open_url(url)    match = re.compile('<div class="thumb" id="post-.+?">(.+?)</div>').findall(link)    for links in match:        title = re.compile('title="(.+?)"').findall(links)[0].replace('Online', '')        url = re.compile('<a href="(.+?)"').findall(links)[0]        icon = re.compile('src="(.+?)"').findall(links)[0]        satools.addDir("[COLOR silver]" + title + "[/COLOR]",url,36,icon,fanart,'')        def SCRAPE_NEWMOVIESONLINE_LINKS(url):    link = satools.open_url(url)    match = re.compile('<div class="video_info">(.+?)</div>').findall(link)[0]    grab = re.compile ('<a href="(.+?)".+?</a>').findall(match)    for url in grab:        if 'http' in url:            satools.addLink("[COLOR silver]" + "Play Movie" + "[/COLOR]",url,2,icon,fanart,'')